package com.mem.vo.business.base.model.po;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.util.Date;/** * <br> * <b>功能：</b>User实体类<br> */@Data@AllArgsConstructor@NoArgsConstructor@Builderpublic class User {    /**     * 主键     */    private Long id;    /**     * 手机号     */    private String phoneNumber;    /**     * 业务主键     */    private String bizCode;    /**     * 用户名     */    private String name;    /**     * 密码     */    private String password;    /**     * 来源 是哪个端的     */    private String source;    /**     * 哪个端的名称     */    private String sourceName;    /**     * 用户状态     */    private Integer status;    /**     *     */    private Date createTime;    /**     *     */    private String createUser;    /**     *     */    private Date updateTime;    /**     *     */    private String updateUser;    /**     * 删除标识  0 有效 1 无效     */    private Integer isDelete;    /**     *     */    private Date ts;    /**     * 管理端角色 0 普通用户  1 超级管理员 2 管理员     */    private Integer role;    /**     * 小程序端普通用户来源     */    private Integer form;    /**     * 是否授权 0 未授权 1 已授权     */    private Integer isAuthorize;    /**     * 性别 0:待完善；1:男；2:女     */    private Integer gender;    /**     * 出生日期     */    private Date birthday;    /**     * 积分     */    private Integer integral;    private Integer rankintegral;    private Integer integralflag;    private String avatarurl;    private Integer sort;    private Integer whocount;    private Integer actcount;    private Integer address;    private String time;    private Integer isaddress;    private Integer overdata;}